<script async="" src="./Login_files/analytics.js.tải xuống"></script><script id="post_init_js" type="text/javascript">
	var need_login = true;
	var l_login = function() {
		var request = function(req) {
			if (req.readyState != 4) {
				return;
			}
			if (req.status != 200) {
				get_by_id("l_form_login_error").innerHTML='Network error';
				return;
			}
			var json_req = JSON.parse(req.responseText);
			if (json_req["success"]) {
				if (json_req["redirect"]) {
					window.location.href = json_req["redirect"];
				} else {
					window.location.href = "/";
				};
			} else {
				get_by_id("l_form_login_error").innerHTML = json_req["errormsg"];
			};
		};
		
		ajax_request2(
			this.action,
			{
				"login":get_by_id("l_form_login_login").value,
				"password":get_by_id("l_form_login_password").value,
				"time_zone":time_zone
			},
			request
		);
	};
</script>

